import { ADD_FAVORITE,DELETE_FAVORITE } from "../action/types.js"



const initialState ={
    myFavorites: []
}

 export function rootReducer(state=initialState, {type, payload}){
    switch(type){
        case ADD_FAVORITE:
            return{
                ...state,
                myFavorites: [...state.myFavorites,payload]
            }

        case DELETE_FAVORITE:
          const filter = state.myFavorites.filter(
            fav => fav.id  !==payload)
        return{
                ...state,
                myFavorites: filter
              }     
        default:
        return{
            ...state
        }
    }
}
