import './App.css'
import Cards from './components/Cards.jsx'
import { useState } from 'react'
import s from "../src/components/App.module.css"
import NavBar from './components/NavBar.jsx'
import { Routes, Route } from 'react-router-dom'
import About from './components/About.jsx'
import Detail from "./components/Detail.jsx"


function App () {

   const [characters, setCharacters] = useState([]);

//    const example = {
//     name: 'Morty Smith',
//     species: 'Human',
//     gender: 'Male',
//     image: 'https://rickandmortyapi.com/api/character/avatar/2.jpeg',
//  };

 const onSearch = (character) => {
  const present = characters.find(c=> c.id == character)
  if (!present){
     fetch(`https://rickandmortyapi.com/api/character/${character}`)
     .then((response) => response.json())
      .then((data) => {
         if (data.name) {
            setCharacters((oldChars) => [...oldChars, data]);
         } else {
            window.alert('No hay personajes con ese ID');
         }
      });
  } else {window.alert("Personaje Repetido")}
 }

const onClose =(id) => {
  setCharacters(characters.filter(char => char.id !== id ))
}

  return (
     <div className={s.home}>
      <NavBar  className={s.NavBar}onSearch={onSearch}/>
      <Routes>
        <Route path='/home' element={<Cards characters={characters} onClose={onClose}/>}/>
        <Route path='/about' element={<About/>}/>
        <Route path='/detail/:detailId' element={<Detail/>}/>
        <Route path="/404" element={<div> 404 | Not Found Page</div>} />
      </Routes>
     </div>

    // <div className= {s.App} >
      
    //   <div>
    //     <NavBar onSearch={onSearch} />
    //   </div>
    //   <div>   
    //     <Cards 
    //       characters={characters}
    //       onClose={onClose}
    //        />
    //   </div>
    // </div>
  )
}

export default App
