import React from "react";
import s from "../CARD/Card.module.css"
import { Link } from "react-router-dom";


export default function Card(props) {
   return (
      <div className={s.contenedor}>
         <button  className={s.button} onClick={() => props.onClose(props.id)}>X</button>
         <Link to={`/detail/${props.id}`}><h2 className={s.name}>{props.name}</h2></Link>
         <h2 className={s.species}>{props.species}</h2>
         <h2 className={s.gender}>{props.gender}</h2>
         <img className={s.imagen} src={props.image} alt="" />
      </div>
   );
}
