import s from "../Card/Card.module.css"
import React from "react";
import { Link } from "react-router-dom"

export default function Card(props) {
   return (
      <div className={s.contenedor}>
         <button className={s.btn} onClick={() => props.onClose(props.id)}>X</button>
         <Link to={`/detail/${props.id}`}><h2 className={s.txt}>{props.name}</h2></Link>
         <img className={s.img} src={props.image} alt="" />
         <div className={s.txtAbajo}>
         <h2 className={s.txt}>{props.species}</h2>
         <h2 className={s.txt}>{props.gender}</h2>
         </div>
      </div>
   );
}
