import './App.css'
import Cards from "./components/CARDS/Cards.jsx"
import { useState } from 'react'
import s from "../src/components/App.module.css"
import NavBar from './components/NAVBAR/NavBar.jsx'
import { Routes, Route } from 'react-router-dom'
import About from './components/ABOUT/About.jsx'
import Detail from "./components/DETAIL/Detail.jsx"
import Error from "./components/ERROR/Error.jsx"
import LandingPage from "./components/LANDING-PAGE/LandingPage.jsx"
import Form from './components/FORM/Form'
import { useLocation } from 'react-router-dom'



function App () {
   const location = useLocation();
   const [characters, setCharacters] = useState([]);

//    const example = {
//     name: 'Morty Smith',
//     species: 'Human',
//     gender: 'Male',
//     image: 'https://rickandmortyapi.com/api/character/avatar/2.jpeg',
//  };

 const onSearch = (character) => {
  const present = characters.find(c=> c.id == character)
  if (!present){
     fetch(`https://rickandmortyapi.com/api/character/${character}`)
     .then((response) => response.json())
      .then((data) => {
         if (data.name) {
            setCharacters((oldChars) => [...oldChars, data]);
         } else {
            window.alert('No hay personajes con ese ID');
         }
      });
  } else {window.alert("Personaje Repetido")}
 }

const onClose =(id) => {
  setCharacters(characters.filter(char => char.id !== id ))
}

  return (
     <div className={s.home}>
      {!["/","/form"].includes(location.pathname) && <NavBar  className={s.NavBar}onSearch={onSearch}/>}
      <Routes>
        <Route  exact path="/" element={<LandingPage/>}/>
        <Route path='/form' element={<Form/>}/>
        <Route path='/home' element={<Cards characters={characters} onClose={onClose}/>}/>
        <Route path='/about' element={<About/>}/>
        <Route path='/detail/:detailId' element={<Detail/>}/>
        <Route path="/*" element={<Error/>} />
      </Routes>
     </div>

    // <div className= {s.App} >
      
    //   <div>
    //     <NavBar onSearch={onSearch} />
    //   </div>
    //   <div>   
    //     <Cards 
    //       characters={characters}
    //       onClose={onClose}
    //        />
    //   </div>
    // </div>
  )
}

export default App
