import React from "react";
import { useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import s from "../DETAIL/Detail.module.css"




export default function Detail(){
    const [character, setCharacter] = useState({})
    const {detailId} = useParams();
    
    useEffect(() => {
      fetch(`https://rickandmortyapi.com/api/character/${detailId}`)
           .then((response) => response.json())
           .then((char) => {
              if (char.name) {
                 setCharacter(char);
                 console.log(char)
              } else {
                 window.alert('No hay personajes con ese ID');
              }
           })
           .catch((err) => {
              window.alert('No hay personajes con ese ID');
           });
        return setCharacter({});
     }, [detailId]);


     return(
        <div>
            <h1 className={s.text}>{character.name}</h1>
            <h2 className={s.text}>{character.status}</h2>
            <h2 className={s.text}>{character.species}</h2>
            <h2 className={s.text}>{character.gender}</h2>
            <h2 className={s.text}>{character.origin?.name}</h2>
            <img src={character.image}/>
            <br/>
            <Link to="/home"><button>Home</button></Link>
        </div>
     )

}
