import { ADD_FAVORITE,DELETE_FAVORITE, ORDER, FILTER } from "../action/types.js"



const initialState ={
    myFavorites: [],
    allCharacters: [],
}

 export default function rootReducer(state=initialState, {type, payload}){
    switch(type){
        case ADD_FAVORITE:
            return{
                ...state,
                allCharacters: [...state.allCharacters, payload],
                myFavorites: [...state.myFavorites,payload]
            }

        case DELETE_FAVORITE:
          const filter = state.myFavorites.filter(
            fav => fav.id  !==payload)
        return{
                ...state,
                myFavorites: filter
              } 
              
         
        case FILTER:
            const filterCopy = [...state.allCharacters];
            const filters =filterCopy.filter(char => char.gender === payload)      
        return{
            ...state,
            myFavorites: filters,
        }

        case ORDER: 
              const orderCopy = [...state.allCharacters];
              const order = orderCopy.sort ((a,b) => {
                if (a.id > b.id){
                     return payload === "Ascendente" ? 1 : -1
                }
                if (a.id < b.id){ 
                    return payload === "Ascendente" ? -1 :1
                }
                else  return 0
              })

              return {
                ...state,
                myFavorites: order
              }
            default:
        return{
            ...state
        }
    }
}
